mapper.update:
UPDATE usercate
SET name=#{name}, seqno=#{seqno}, visible=#{visible}
WHERE usercateno = #{usercateno}

mapper.select:
SELECT usercateno, name, seqno, visible, rdate, cnt
FROM USERCATE
WHERE usercateno = #{usercateno}

mapper.update:
UPDATE usercate
SET seqno = seqno - 1
WHERE usercateno=#{usercateno}

UPDATE usercate
SET seqno = seqno + 1
WHERE usercateno=#{usercateno}
